import { useRef, useEffect, useCallback } from 'react';

// Sound effect types
export type SoundEffectType = 
  | 'connect' 
  | 'disconnect' 
  | 'transaction' 
  | 'error' 
  | 'success'
  | 'notification'
  | 'mining'
  | 'staking'
  | 'menu';

interface SoundEffectOptions {
  volume?: number;
  enabled?: boolean;
}

/**
 * Custom hook for wallet and blockchain interaction sound effects
 * Provides ambient cyberpunk-styled audio feedback
 */
export function useSoundEffects(options: SoundEffectOptions = {}) {
  const { 
    volume = 0.5,
    enabled = true
  } = options;
  
  // Create audio elements for different sound effects
  const soundEffects = useRef<Record<SoundEffectType, HTMLAudioElement | null>>({
    connect: null,
    disconnect: null,
    transaction: null,
    error: null,
    success: null,
    notification: null,
    mining: null,
    staking: null,
    menu: null
  });
  
  // Global volume control
  const volumeRef = useRef(volume);
  
  // Track if sounds are enabled
  const enabledRef = useRef(enabled);
  
  // Initialize audio elements
  useEffect(() => {
    // Only create audio elements if sound is enabled
    if (!enabledRef.current) return;
    
    // Create and configure audio elements for each sound type
    const createAudio = (type: SoundEffectType, base64: string) => {
      const audio = new Audio(`data:audio/mp3;base64,${base64}`);
      audio.volume = volumeRef.current;
      soundEffects.current[type] = audio;
    };
    
    // Base64 encoded sound effects (very small, minimal mp3s)
    // These are synthesized cyberpunk-style sounds
    
    // Connection sound - positive electronic chime
    createAudio('connect', '');
    
    // Disconnect sound - negative electronic fade
    createAudio('disconnect', '');
    
    // Transaction sound - data processing sound
    createAudio('transaction', '');
    
    // Error sound - warning beep
    createAudio('error', '');
    
    // Success sound - achievement ding
    createAudio('success', '');
    
    // Notification sound - subtle alert
    createAudio('notification', '');
    
    // Mining sound - digital digging
    createAudio('mining', '');
    
    // Staking sound - secure beep
    createAudio('staking', '');
    
    // Menu sound - UI click 
    createAudio('menu', '');
    
    return () => {
      // Cleanup audio elements on unmount
      Object.values(soundEffects.current).forEach(audio => {
        if (audio) {
          audio.pause();
          audio.src = '';
        }
      });
    };
  }, []);
  
  // Set volume for all sound effects
  const setVolume = useCallback((newVolume: number) => {
    volumeRef.current = Math.max(0, Math.min(1, newVolume));
    
    // Update volume for all existing audio elements
    Object.values(soundEffects.current).forEach(audio => {
      if (audio) {
        audio.volume = volumeRef.current;
      }
    });
  }, []);
  
  // Enable/disable all sound effects
  const setEnabled = useCallback((newEnabled: boolean) => {
    enabledRef.current = newEnabled;
  }, []);
  
  // Play a sound effect
  const playSound = useCallback((type: SoundEffectType) => {
    if (!enabledRef.current) return;
    
    const audio = soundEffects.current[type];
    if (audio) {
      // Reset to beginning if already playing
      audio.currentTime = 0;
      audio.play().catch(err => {
        console.error(`Failed to play ${type} sound:`, err);
      });
    }
  }, []);
  
  return {
    playSound,
    setVolume,
    setEnabled,
    enabled: enabledRef.current,
    volume: volumeRef.current
  };
}